sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var array = newValue.split(',');
	if(array.length > 0){
	   for(i=0; i < array.length ; i++){
		   //g_form.addInfoMessage('array in position ' + i + ' is ' + array[i]);
		   if(array[i] == 'Wireless'){
			   g_form.setDisplay('u_wireless_protocols_used', true);
			   break;
		   } else {
			   g_form.setValue('u_wireless_protocols_used', '');
			   g_form.setDisplay('u_wireless_protocols_used', false);
			   break;
		   }

	   }
	}	

}
