	g_form.setValue('u_secure_email', '');
	g_form.setValue('u_physical_media', '');
	g_form.setValue('u_review_type_other', '');
	g_form.setValue('u_data_transfer_comments', '');


	g_form.setDisplay('u_sftp', false);
	g_form.setDisplay('u_ftp_s', false);
	g_form.setDisplay('u_https_s', false);
	g_form.setDisplay('u_secure_email', false);
	g_form.setDisplay('u_physical_media', false);
	g_form.setDisplay('u_review_type_other', false);
	g_form.setDisplay('u_data_transfer_comments', false);
}

